import "./App.css";
import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBus, faTrain } from "@fortawesome/free-solid-svg-icons";

function App() {
  const [forsinkelser, setForsinkelser] = useState([]);

  async function test() {
    fetch("http://localhost:3001/forsinkelser")
      .then((res) => res.json())
      .then((data) => {
        setForsinkelser(data);
      });
  }

  test();

  return (
    <div className="App">
      <div className="header">
        <input className="search" type="text"></input>
        <div className="refresh">
          <i className="fa-solid fa-arrow-rotate-right refresh"></i>
        </div>
      </div>
      {forsinkelser.map(
        (forsinkelse) => (
          forsinkelser.sort((a, b) => {
            return new Date(b.aimedTime) - new Date(a.aimedTime);
          }),
          (
            <div className="forsinkelseBox" key={forsinkelse.id + {} + forsinkelse.aimedTime}>
              <div className="lineBox">
                {forsinkelse.line.startsWith("L") ? <FontAwesomeIcon icon={faTrain}></FontAwesomeIcon> : <FontAwesomeIcon icon={faBus}></FontAwesomeIcon>}
                <i className="fa-solid fa-train-subway train"></i>
                <span>{forsinkelse.line} </span>
                <span>{forsinkelse.name}</span>
              </div>
              <div className="textElems">
                <p className="expectedBox">
                  Ankom Stasjonen:{" "}
                  {new Date(forsinkelse.expectedTime).toTimeString().slice(0, 5) + " " + new Date(forsinkelse.expectedTime).getDate() + "/" + (new Date(forsinkelse.expectedTime).getMonth() + 1)}
                </p>
                <p className="diffBox">Forsinka med: {Math.round((((Date.parse(forsinkelse.expectedTime) - Date.parse(forsinkelse.aimedTime)) % 86400000) % 3600000) / 60000)} min</p>
              </div>
            </div>
          )
        )
      )}
    </div>
  );
}

export default App;
